package sectionThree;

public class floatingPointNumbers 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		// floating numbers are decimal points
		
		//single precision ==> floating numbers
		
		//double precisions ==> double numbers
		
		float myMinFloat = Float.MIN_VALUE;
		float myMaxFloat = Float.MAX_VALUE; 
		System.out.println("Float min value is: " + 
				myMinFloat); 
		System.out.println("Float MAX value is: " + 
				myMaxFloat); 
		
		int myIntVal = 5;
		float muFloatVal = 5.25f;
		double myDoubleVal = 5.25d; 
		
		float myNewFloatVal = (float)5.25; 
		
		System.out.println("my int value " + myIntVal);
		System.out.println("float val " + muFloatVal);
		System.out.println("double val " + myDoubleVal);
		
		
		

	}

}
